export default function QueryTable<T>({
  table,
  filters,
  isLoading = false,
  isError = false,
  error,
  data,
  refetch,
  colsLength = 0,
  selectedRowsActions,
  contextMenuContent,
}: {
  table: ReactTable<T>;
  filters?: Record<string, any>;
  isLoading?: boolean;
  isError?: boolean;
  error?: any;
  data?: T[];
  refetch: (filters?: Record<string, any>) => void;
  colsLength?: number;
  selectedRowsActions: React.ReactNode;
  contextMenuContent?: (item: T) => React.ReactNode;
}) {
  return (
    <>
      <PageLayoutTable className="text-nowrap">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <TableHead
                    className={cn(
                      "text-left text-nowrap min-w-max",
                      (header.id === "select" || header.id === "actions") &&
                        "w-16"
                    )}
                    key={header.id}
                  >
                    {header.isPlaceholder
                      ? null
                      : flexRender(
                          header.column.columnDef.header,
                          header.getContext()
                        )}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {isLoading ? (
              [...Array(6)].map((_, index) => (
                <TableRow key={`table-skeleton-${index}`}>
                  {[...Array(colsLength)].map((_, index) => (
                    <TableCell className="text-center" key={`cell-${index}`}>
                      <Skeleton className="w-full min-w-5 h-5" />
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : isError ? (
              <TableRow>
                <TableCell colSpan={colsLength}>
                  <div className="flex flex-col items-center justify-center py-6 gap-5">
                    <TbServerBolt className="size-10 text-destructive" />
                    {(error as any)?.response?.data?.message ??
                      "Failed to fetch data. Please try again."}
                    <Button
                      variant="secondary"
                      className="flex gap-2"
                      onClick={() => refetch(filters)}
                    >
                      <TbReload className="size-5" /> Retry
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ) : table.getRowModel().rows?.length === 0 || data?.length === 0 ? (
              <TableRow>
                <TableCell colSpan={colsLength}>
                  <div className="flex flex-col items-center justify-center py-6">
                    <EmptyIcon />
                    No data
                  </div>
                </TableCell>
              </TableRow>
            ) : (
              table.getRowModel().rows.map((row) => (
                <ContextMenu key={row.id}>
                  <ContextMenuTrigger asChild disabled={!contextMenuContent}>
                    <TableRow key={row.id}>
                      {row.getVisibleCells().map((cell) => (
                        <TableCell key={cell.id}>
                          {flexRender(
                            cell.column.columnDef.cell,
                            cell.getContext()
                          )}
                        </TableCell>
                      ))}
                    </TableRow>
                  </ContextMenuTrigger>
                  {contextMenuContent && (
                    <ContextMenuContent className="w-48">
                      {contextMenuContent(row.original)}
                    </ContextMenuContent>
                  )}
                </ContextMenu>
              ))
            )}
          </TableBody>
        </Table>
      </PageLayoutTable>
      {table.getSelectedRowModel().rows.length > 0 && (
        <div className="sticky bottom-0 inset-x-0 z-10 mt-2.5 flex flex-col sm:flex-row w-full sm:items-center sm:justify-between rounded-md border border-muted px-5 py-3.5 text-primary-dark shadow-sm bg-background gap-2">
          <div>
            <strong className="font-bold">
              {table.getSelectedRowModel().rows.length}
            </strong>{" "}
            item(s) selected
            <Button
              variant="link"
              size="xs"
              onClick={() => table.resetRowSelection()}
              className="inline-flex"
            >
              Clear selection
            </Button>
          </div>
          <div className="flex gap-4">{selectedRowsActions}</div>
        </div>
      )}
    </>
  );
}
